import React from "react";

class BadgeForm extends React.Component {
  
  /**
   * Handle para cambios de los atributos del formulario.
   * @param {Object} e - evento de cambio capturado
   */
  handleChange = (e) => {
    // console.log({ name: e.target.name, value: e.target.value });
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  /**
   * Handle para el evento de clic sobre el botÃ³n.
   * @param {Object} e - evento de clic capturado
   */
  handleClick = (e) => {
    console.log("Button was clicked");
  };

  /** 
   * Prevenir el submit por default de los formularios .
   * @param {Object} e - evento de submit capturado
  */
  handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form was submitted");
  };

  /**
   * Render function.
   */
  render() {
    return (
      <div>
        <h1>New Attendant</h1>
        <form onSubmit={this.handleSubmit} action="">
          <div className="form-group">
            <label htmlFor="">First Name</label>
            <input
              onChange={this.handleChange}
              className="form-control"
              type="text"
              name="firstName"
            />
            <label htmlFor="">Last Name</label>
            <input
              onChange={this.handleChange}
              className="form-control"
              type="text"
              name="lastName"
            />
            <label htmlFor="">E-mail</label>
            <input
              onChange={this.handleChange}
              className="form-control"
              type="email"
              name="email"
            />
            <label htmlFor="">Job Title</label>
            <input
              onChange={this.handleChange}
              className="form-control"
              type="text"
              name="jobTitle"
            />
            <label htmlFor="">Twitter</label>
            <input
              onChange={this.handleChange}
              className="form-control"
              type="text"
              name="twitter"
            />
          </div>
          <button onClick={this.handleClick} className="btn btn-primary">
            Save
          </button>
        </form>
      </div>
    );
  }
  on;
}

export default BadgeForm;
